var url_root = '/';

$(function(){
    var g_fields = ["order_key", "school", "sector", "server"];
    $.each(g_fields, function(i, f){
        $('#' + f).find('li').each(function(){
            if($(this).attr('data-txt') == $('input[name=' + f+ ']').val()){
                $(this).click();
            };
        });
    });

    $('#query_form').submit(function(){
        $.each(g_fields, function(i, f){
            $('#' + f).find('li').each(function(){
                if($(this).hasClass('active')){
                    $('input[name=' + f+ ']').val($(this).attr('data-txt'));
                }
            });
        });
    });
});
